type Story = StoryObj<typeof Messenger>;

// Ein einfacher Wrapper, der die Breite/Umgebung simuliert
function PreviewShell(props: React.ComponentProps<typeof Messenger>) {
  const ref = useRef<MessengerHandle>(null);
  const [loading, setLoading] = useState(false);

  return (
    <div style={{
      height: "100vh",
      width: "100%",
      display: "grid",
      placeItems: "center",
      background: "var(--layout-content-bg, #f5f6f8)"
    }}>
      <div style={{
        width: 960,
        maxWidth: "95vw",
        height: "80vh",
      }}>
        <Messenger
          ref={ref}
          {...props}
          isLoading={loading}
          onSend={async ({ text, isImage, settings }) => {
            setLoading(true);
            console.log("[Preview] onSend", { text, isImage, settings });
            await new Promise(r => setTimeout(r, 600));
            setLoading(false);
            ref.current?.addMessages([
              {
                id: crypto.randomUUID(),
                type: "bot",
                content: `Echo: ${text}`,
                createdAt: Date.now(),
              }
            ]);
          }}
        />
      </div>
    </div>
  );
}

export const Preview: Story = {
  render: (args) => <PreviewShell {...args} />,
};

